package com.example.adventofcode.year2022.day06;

import org.junit.jupiter.params.ParameterizedTest;
import org.junit.jupiter.params.provider.Arguments;
import org.junit.jupiter.params.provider.MethodSource;

import java.util.stream.Stream;

import static org.junit.jupiter.api.Assertions.assertEquals;

class TuningTroubleTest {
    private static Stream<Arguments> filepathsAndExpectedNumberOfFullyInclusiveIntervals() {
        return Stream.of(
                Arguments.of("mjqjpqmgbljsphdztnvjfqwrcgsmlb", 4, 7),
                Arguments.of("bvwbjplbgvbhsrlpgdmjqwftvncz", 4, 5),
                Arguments.of("nppdvjthqldpwncqszvftbrmjlhg", 4, 6),
                Arguments.of("nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg", 4, 10),
                Arguments.of("zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw", 4, 11),

                Arguments.of("mjqjpqmgbljsphdztnvjfqwrcgsmlb", 14, 19),
                Arguments.of("bvwbjplbgvbhsrlpgdmjqwftvncz", 14, 23),
                Arguments.of("nppdvjthqldpwncqszvftbrmjlhg", 14, 23),
                Arguments.of("nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg", 14, 29),
                Arguments.of("zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw", 14, 26),

                Arguments.of("",
                        4, 1850),
                Arguments.of("",
                        14, 2823)
        );
    }

    @ParameterizedTest
    @MethodSource("filepathsAndExpectedNumberOfFullyInclusiveIntervals")
    void testCalculateMarkerPositionFor(final String input,
                                        final int markerLength,
                                        final int expectedMarkerPosition) {
        assertEquals(expectedMarkerPosition, TuningTrouble.calculateMarkerPositionFor(input, markerLength));
    }
}